import React, { useState } from 'react'

interface ImageWithFallbackProps {
  src?: string
  alt: string
  className?: string
  fallbackType?: 'character' | 'weapon' | 'artifact' | 'enemy' | 'material' | 'domain' | 'food' | 'animal'
}

// Placeholders simples con emojis
const PLACEHOLDERS = {
  character: 'data:image/svg+xml;base64,',
  weapon: 'data:image/svg+xml;base64,',
  artifact: 'data:image/svg+xml;base64,',
  enemy: 'data:image/svg+xml;base64,',
  material: 'data:image/svg+xml;base64,',
  domain: 'data:image/svg+xml;base64,',
  food: 'data:image/svg+xml;base64,',
  animal: 'data:image/svg+xml;base64,'
}

const ImageWithFallback: React.FC<ImageWithFallbackProps> = ({
  src,
  alt,
  className = '',
  fallbackType = 'character'
}) => {
  const [imgSrc, setImgSrc] = useState<string | undefined>(src)
  const [hasError, setHasError] = useState(false)

  const handleError = () => {
    if (!hasError) {
      setHasError(true)
      setImgSrc(PLACEHOLDERS[fallbackType])
    }
  }

  // Función para construir la URL completa de la imagen
  const getImageUrl = (url: string | undefined): string => {
    if (!url) return PLACEHOLDERS[fallbackType]
    
    // Si ya es una URL completa, usarla directamente
    if (url.startsWith('http')) return url
    
    // Si es una ruta relativa, construir la URL completa
    // Las imágenes de genshin-db suelen ser relativas al dominio de Genshin Impact
    if (url.startsWith('/')) return `https://genshin.honeyhunterworld.com${url}`
    
    return url
  }

  return (
    <img
      src={getImageUrl(imgSrc)}
      alt={alt}
      className={className}
      onError={handleError}
      loading="lazy"
    />
  )
}

export default ImageWithFallback